package cn.com.geovis;

public class SingleChatAudioDataEncoder extends AbstractSingleChatDataEncoder {

	public static final byte MESSAGE_TYPE = 2;
	
	@Override
	protected byte messageType0() {
		return MESSAGE_TYPE;
	}

	@Override
	protected String dataProperty() {
		return "data";
	}

	@Override
	protected String subtype() {
		return "audio";
	}
	
	public static void main(String[] args) {

		String msg = "{id:'dad44267-91b7-4c4d-a640-5cf5a48c0924',type:'chat',subType:'audio',receiver:123,sender:456,sendTime:1601347381497,data:{data:'data:image/png;base64,'}}";
		System.out.println("编码后的数据长度是" + ByteUtils.stringToByte(msg).length);

		DataEncoder data = new DataEncoder();
		byte[] datas = data.encode(msg);
		System.out.println("编码后的数据长度是" + datas.length);

		String smsg = data.decode(datas);
		System.out.println("数据还原:" + smsg);
	}

	

}
